import React, { useState } from 'react';
import Button from '../component/Button';

interface movieListProps {
    init: string[];
    list: string[];
    onSearch: (searchTerm: string[]) => void;
}

const Search: React.FC<movieListProps> = ({init, list, onSearch}) => {

    const [value, setValue] = useState('');

    const handleInputChange = (e: any) => {
        setValue(e.target.value);
    };

    const handleClick = () => {
        filteredData();
    };

    const handleReset = () => {
        setValue('');
        onSearch(init);
    };

    const filteredData = () => {
        let result = value !== '' ? list.filter(
            (movie: any) =>
                movie.title.toLowerCase().includes(value.toString().toLowerCase()) || movie.genre.toLowerCase().includes(value.toString().toLowerCase())
        ) : init
        onSearch(result);
    };

    return (
        <div>
            <input
                type="text"
                value={value}
                onChange={handleInputChange}
                placeholder="Search movie"
            />
            <Button onClick={handleClick}>Search</Button>
            <Button onClick={handleReset}>Reset</Button>
        </div>
    );
}

export default Search;
